import React , {useState} from 'react'


export default function Textform(props) {
    const [text , setText] = useState("Enter the text here");

    const handleUpcase = ()=>{
        // console.log("button clicked"+ text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLowcase = ()=>{
      let newText = text.toLowerCase();
      setText(newText);
    }

    const handleonChange = (event)=>{
        // console.log("on change");
        setText(event.target.value);
    }

    return (
        <> 
    <h3>{props.heading}</h3>
    <div className="mb-3">
    <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
    <input type="email" onChange={handleonChange} className="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
  </div>
  <div className="mb-3">
    <label htmlFor="exampleFormControlTextarea1" className="form-label">Example textarea</label>
    <textarea className="form-control" value = {text} onChange={handleonChange}
     id="exampleFormControlTextarea1" rows={3} defaultValue={""} />
  </div>
    <button className='btn btn-primary' onClick={handleUpcase} mx-2>Convert To Uppercase</button>
    <button className='btn btn-primary' onClick={handleLowcase} onChange = {handleonChange} mx-2>Convert To Lowercase</button>


    <div className="container my-3">
        <h1>Your text summary</h1>
        <p>{text.split(" ").length} words and {text.length} characters </p>
        <p>{0.008 * text.split(" ").length} minutes read</p>
        <h2>Preview</h2>
        <p>{text}</p>
    </div>
  </>
  )
}
