import React , {useState} from 'react'


export default function Textform(props) {
    const [text , setText] = useState("Enter the text here");

    const handleUpcase = ()=>{
        // console.log("button clicked"+ text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleonChange = (event)=>{
        // console.log("on change");
        setText(event.target.value);
    }

    return (
        <> 
    <h3>{props.heading}</h3>
    <div className="mb-3">
    <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
    <input type="email" onChange={handleonChange} className="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
  </div>
  <div className="mb-3">
    <label htmlFor="exampleFormControlTextarea1" className="form-label">Example textarea</label>
    <textarea className="form-control" value = {text} onChange={handleonChange}
     id="exampleFormControlTextarea1" rows={3} defaultValue={""} />
  </div>
    <button className='btn btn-primary' onClick={handleUpcase}>Convert To Uppercase</button>

  </>
  )
}
