function App() {
const [mode , setMode] = useState("light");

const toggleMode = ()=>{
  if(mode==='light')
  {
    setMode("dark");
  }
  else{
    setMode('light');
  }
}

    return (
      <>

      <Navbar title = "textutils" mode={mode} toggleMode = {toggleMode}/>
      <div className="container">
        {/* <Textform heading="Enter text to analyse"/> */}
      </div>
      <About className="about"/>
      </>
    );
  }


export default App;
